// 1. GET BOTH "TRASH LISTS"
const silenceCuts = $('Detect Silence').item.json.response; // List A: Silences
const aiCuts = JSON.parse($input.first().json.output);      // List B: Bad Takes

// Helper: Convert "00:00:00.000" to Seconds
function timeToSeconds(timeStr) {
    if (!timeStr) return 0;
    const [h, m, s] = timeStr.replace(',', '.').split(':');
    return (parseFloat(h) * 3600) + (parseFloat(m) * 60) + parseFloat(s);
}

// Helper: Convert Seconds back to Timestamp
function secondsToTime(totalSeconds) {
    const h = Math.floor(totalSeconds / 3600);
    const m = Math.floor((totalSeconds % 3600) / 60);
    const s = (totalSeconds % 60).toFixed(3);
    return `${String(h).padStart(2, '0')}:${String(m).padStart(2, '0')}:${String(s).padStart(6, '0')}`;
}

// 2. COMBINE & SORT
// We convert everything to seconds for easy math
let allCuts = [
    ...silenceCuts.map(c => ({ start: timeToSeconds(c.start), end: timeToSeconds(c.end) })),
    ...aiCuts.map(c => ({ start: timeToSeconds(c.start), end: timeToSeconds(c.end) }))
];

// Sort by start time (Crucial for merging)
allCuts.sort((a, b) => a.start - b.start);

// 3. MERGE OVERLAPS
// Example: If Silence is 0-5s and AI says cut 4-6s, we simply cut 0-6s.
const mergedCuts = [];
if (allCuts.length > 0) {
    let current = allCuts[0];

    for (let i = 1; i < allCuts.length; i++) {
        const next = allCuts[i];

        if (next.start < current.end) {
            // Overlap detected! Extend the current cut if needed.
            current.end = Math.max(current.end, next.end);
        } else {
            // No overlap. Push current and move to next.
            mergedCuts.push(current);
            current = next;
        }
    }
    mergedCuts.push(current);
}

// 4. OUTPUT FINAL JSON
return {
    "response": mergedCuts.map(c => ({
        "start": secondsToTime(c.start),
        "end": secondsToTime(c.end)
    }))
};

