={{
  (() => {
    // 1. HELPER: Fix URLs
    function getDirectUrl(url) {
      if (!url) return '';
      if (url.includes('dropbox.com')) return url.replace('www.dropbox.com', 'dl.dropboxusercontent.com').replace(/(\?|&)dl=\d/, '') + '?dl=1';
      if (url.includes('drive.google.com') && url.includes('/d/')) {
         const match = url.match(/\/d\/([a-zA-Z0-9_-]+)/);
         if (match && match[1]) return `https://drive.google.com/uc?export=download&id=${match[1]}`;
      }
      return url;
    }

    // 2. GATHER ASSETS
    // Safely get the video link from the Add Caption node
    const captionNode = $('Add Caption').first();
    const rawVideoUrl = captionNode.json.body ? captionNode.json.body.response : captionNode.json.response;
    const videoUrl = getDirectUrl(rawVideoUrl);

    const musicUrl = getDirectUrl($('Editing Trigger').first().json.fields.Music);

    // Clean Title
    const rawTitle = $('Editing Trigger').first().json.fields.Title || "WATCH UNTIL THE END";
    const cleanTitle = rawTitle
      .replace(/\\n/g, '\n')       // Allow manual "\n"
      .replace(/'/g, "\u2019")     // Smart quote (Safe for FFmpeg)
      .replace(/:/g, "\\:")        // Escape colons
      .replace(/%/g, "\\\\%")      // Escape percents
      .replace(/\\/g, '\\\\')      // Escape backslashes
      // Auto-insert line breaks every ~20 chars
      .replace(/(?![^\n]{1,20}$)([^\n]{1,20})\s/g, '$1\n'); 

    // 3. GENERATE FFMPEG FILTER
    const filterComplex = [
      // Step A: Draw Title (First 3s, Center, Red, Big)
      `[0:v]drawtext=text='${cleanTitle}':font='The Bold Font':fontcolor=red:fontsize=90:x=(w-text_w)/2:y=(h-text_h)/2:enable='between(t,0,3)':shadowcolor=black:shadowx=5:shadowy=5[v_out]`,

      // Step B: Prepare Music (Volume 10%)
      `[1:a]volume=0.1[bgm]`,

      // Step C: Mix Audio
      `[0:a][bgm]amix=inputs=2:duration=first[a_out]`
    ].join(';');

    // 4. RETURN OBJECT (Do NOT use JSON.stringify here)
    return {
      "id": "job_" + $execution.id,
      "webhook_url": $execution.resumeUrl,

      "inputs": [
        { "file_url": videoUrl }, // [0]
        { "file_url": musicUrl }  // [1]
      ],

      "global_options": [
        { "option": "-y" }
      ],

      "outputs": [
        {
          "options": [
            { "option": "-filter_complex", "argument": filterComplex },
            { "option": "-map", "argument": "[v_out]" },
            { "option": "-map", "argument": "[a_out]" },
            { "option": "-c:v", "argument": "libx264" },
            { "option": "-preset", "argument": "fast" },
            { "option": "-c:a", "argument": "aac" },
            { "option": "-shortest", "argument": null }
          ]
        }
      ]
    };
  })()
}}
